   /**
    * f0 -> "new"
    * f1 -> "int"
    * f2 -> "["
    * f3 -> Expression()
    * f4 -> "]"
    */
   public R visit(ArrayAllocationExpression n, A argu) {
      R _ret = null;
      String arcode = getNewCode();
      retAttr arrstruct = new retAttr(arcode, "int[]");
      n.f0.accept(this, argu);
      n.f1.accept(this, argu);
      n.f2.accept(this, argu);
      String asize = (String) n.f3.accept(this, argu);
      n.f4.accept(this, argu);
      String aszCode = getNewCode();
      String hszCode = getNewCode();

      print("MOVE " + aszCode + " PLUS " + asize + " 1");
      print("MOVE " + hszCode + " TIMES " + aszCode + " 4");
      print("MOVE " + arcode + " HALLOCATE " + hszCode);
      print("HSTORE " + arcode + " 0 " + aszCode); // store the array size

      String loopLabel = getNewLabel();
      String endLabel = getNewLabel();

      String zcode = getNewCode();
      String icode = getNewCode();
      String aelemCode = getNewCode();
      String statCode = getNewCode();

      print("MOVE " + zcode + " 0");
      print("MOVE " + icode + " 4"); // initial index 4
      print("MOVE" + hszCode + " MINUS " + hszCode + " 4"); // keeping the size as original (ignore the size entry)

      print(loopLabel);

      print("MOVE " + statCode + " LE " + icode + " " + hszCode); // index <= size
      print("CJUMP " + statCode + " " + endLabel);
      print("MOVE " + aelemCode + " PLUS " + icode + " " + arcode); // a + idx*4
      print("MOVE " + aelemCode + " " + zcode); // init
      print("MOVE " + icode + " PLUS " + icode + " 4"); // idx += 1 (4 actually)
      print("JUMP " + loopLabel);

      print(endLabel);
      print("NOOP"); // why??
      code -= 6; // zcode, icode, alemcode, statcode, aszCode, hszCode done

      return (R) arrstruct;
   }

